package MyJava;

public class InorDe {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
		//출력 후 증가
		System.out.println(x++);
		//1 증가 후 출력 
		System.out.println(++x);
		//x를 출력
		System.out.println(x);
		//증가 후 출력 
		System.out.println(++x);
		int y = 12;
		
		System.out.println("\n\n");
		//감소 후 출력 
		System.out.println(--y);
		//출력 후 감소 
		System.out.println(y--);
		//y 출력
		System.out.println(y);
		//출력 후 감
		System.out.println(y--);
	}

}
